import React, { useState } from 'react';
import api from '../config/api';
import '../styles/Exercises.css';

const Exercises = () => {
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [isActive, setIsActive] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);
  const [phase, setPhase] = useState(''); // 'inhale', 'hold', 'exhale'
  const [showModal, setShowModal] = useState(false);
  const [sessionData, setSessionData] = useState({ mood: '', notes: '' });

  const exercises = [
    {
      id: 1,
      name: 'Box Breathing',
      description: 'Breathe in a square pattern. Inhale, hold, exhale, hold.',
      duration: 5,
      pattern: { inhale: 4, hold1: 4, exhale: 4, hold2: 4 },
      icon: 'üì¶'
    },
    {
      id: 2,
      name: '4-7-8 Breathing',
      description: 'Deeply relaxing technique. Inhale for 4, hold for 7, exhale for 8.',
      duration: 5,
      pattern: { inhale: 4, hold1: 7, exhale: 8, hold2: 0 },
      icon: 'üåô'
    },
    {
      id: 3,
      name: 'Deep Breathing',
      description: 'Simple and effective. Slow, deep breaths.',
      duration: 10,
      pattern: { inhale: 5, hold1: 2, exhale: 5, hold2: 2 },
      icon: 'ü´Å'
    }
  ];

  const startExercise = (exercise) => {
    setSelectedExercise(exercise);
    setTimeLeft(exercise.duration * 60);
    setIsActive(true);
    setIsPaused(false);
    runBreathingCycle(exercise.pattern);
  };

  const runBreathingCycle = async (pattern) => {
    const { inhale, hold1, exhale, hold2 } = pattern;
    
    while (isActive && timeLeft > 0 && !isPaused) {
      setPhase('inhale');
      await sleep(inhale * 1000);
      
      if (hold1 > 0) {
        setPhase('hold');
        await sleep(hold1 * 1000);
      }
      
      setPhase('exhale');
      await sleep(exhale * 1000);
      
      if (hold2 > 0) {
        setPhase('hold');
        await sleep(hold2 * 1000);
      }
    }
  };

  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

  React.useEffect(() => {
    let interval = null;
    
    if (isActive && !isPaused && timeLeft > 0) {
      interval = setInterval(() => {
        setTimeLeft((time) => {
          if (time <= 1) {
            stopExercise();
            return 0;
          }
          return time - 1;
        });
      }, 1000);
    }
    
    return () => clearInterval(interval);
  }, [isActive, isPaused, timeLeft]);

  const pauseExercise = () => {
    setIsPaused(!isPaused);
  };

  const stopExercise = () => {
    setIsActive(false);
    setIsPaused(false);
    setShowModal(true);
  };

  const handleSaveSession = async () => {
    try {
      const durationMinutes = Math.ceil((selectedExercise.duration * 60 - timeLeft) / 60);
      
      await api.post('/sessions', {
        exerciseType: selectedExercise.name,
        duration: durationMinutes,
        mood: sessionData.mood,
        notes: sessionData.notes
      });
      
      alert('Session saved successfully! üéâ');
      setShowModal(false);
      setSelectedExercise(null);
      setSessionData({ mood: '', notes: '' });
    } catch (error) {
      console.error('Error saving session:', error);
      alert('Failed to save session. Please try again.');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getPhaseInstruction = () => {
    if (phase === 'inhale') return 'Breathe In üå¨Ô∏è';
    if (phase === 'exhale') return 'Breathe Out üí®';
    if (phase === 'hold') return 'Hold ü§≤';
    return 'Get Ready...';
  };

  return (
    <div className="exercises-container">
      <div className="exercises-header">
        <h1>Breathing Exercises</h1>
        <p>Choose an exercise to begin your mindfulness practice</p>
      </div>

      {!selectedExercise ? (
        <div className="exercises-grid">
          {exercises.map((exercise) => (
            <div key={exercise.id} className="exercise-card">
              <div className="exercise-icon">{exercise.icon}</div>
              <h3>{exercise.name}</h3>
              <p>{exercise.description}</p>
              <div className="exercise-duration">{exercise.duration} minutes</div>
              <button 
                className="btn btn-start"
                onClick={() => startExercise(exercise)}
              >
                Start Exercise
              </button>
            </div>
          ))}
        </div>
      ) : (
        <div className="exercise-active">
          <div className="exercise-info">
            <h2>{selectedExercise.name}</h2>
            <div className="timer">{formatTime(timeLeft)}</div>
          </div>

          <div className={`breathing-circle ${phase}`}>
            <div className="breathing-text">{getPhaseInstruction()}</div>
          </div>

          <div className="exercise-controls">
            <button 
              className="btn btn-pause"
              onClick={pauseExercise}
            >
              {isPaused ? 'Resume' : 'Pause'}
            </button>
            <button 
              className="btn btn-stop"
              onClick={stopExercise}
            >
              Stop
            </button>
          </div>
        </div>
      )}

      {/* Save Session Modal */}
      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>Great job! üéâ</h2>
            <p>How are you feeling?</p>

            <div className="form-group">
              <label>Mood</label>
              <select 
                value={sessionData.mood}
                onChange={(e) => setSessionData({...sessionData, mood: e.target.value})}
                required
              >
                <option value="">Select your mood</option>
                <option value="Calm">üòå Calm</option>
                <option value="Happy">üòä Happy</option>
                <option value="Relaxed">üòé Relaxed</option>
                <option value="Peaceful">üßò Peaceful</option>
                <option value="Energized">‚ö° Energized</option>
              </select>
            </div>

            <div className="form-group">
              <label>Notes (optional)</label>
              <textarea
                value={sessionData.notes}
                onChange={(e) => setSessionData({...sessionData, notes: e.target.value})}
                placeholder="How did this session feel?"
                rows={3}
              />
            </div>

            <div className="modal-actions">
              <button 
                className="btn btn-secondary"
                onClick={() => setShowModal(false)}
              >
                Skip
              </button>
              <button 
                className="btn btn-primary"
                onClick={handleSaveSession}
                disabled={!sessionData.mood}
              >
                Save Session
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Exercises;
