import React, { useState, useEffect } from 'react';
import api from '../config/api';
import '../styles/Tracker.css';

const Tracker = () => {
  const [sessions, setSessions] = useState([]);
  const [stats, setStats] = useState({ totalSessions: 0, totalMinutes: 0, currentStreak: 0 });
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all'); // 'all', 'week', 'month'

  useEffect(() => {
    fetchSessions();
  }, []);

  const fetchSessions = async () => {
    try {
      const response = await api.get('/sessions');
      const { sessions, stats } = response.data;
      setSessions(sessions);
      setStats(stats);
    } catch (error) {
      console.error('Error fetching sessions:', error);
    } finally {
      setLoading(false);
    }
  };

  const deleteSession = async (id) => {
    if (!window.confirm('Are you sure you want to delete this session?')) return;
    
    try {
      await api.delete(`/sessions/${id}`);
      setSessions(sessions.filter(s => s._id !== id));
      
      // Recalculate stats
      const updatedSessions = sessions.filter(s => s._id !== id);
      const totalMinutes = updatedSessions.reduce((sum, s) => sum + s.duration, 0);
      setStats({
        totalSessions: updatedSessions.length,
        totalMinutes,
        currentStreak: updatedSessions.length ? Math.ceil(updatedSessions.length / 7) : 0
      });
      
      alert('Session deleted successfully');
    } catch (error) {
      console.error('Error deleting session:', error);
      alert('Failed to delete session');
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getMoodEmoji = (mood) => {
    const moodMap = {
      calm: 'üòå',
      happy: 'üòä',
      relaxed: 'üòé',
      peaceful: 'üßò',
      energized: '‚ö°',
      stressed: 'üò∞',
      anxious: 'üòü'
    };
    return moodMap[mood.toLowerCase()] || 'üòä';
  };

  const getFilteredSessions = () => {
    if (filter === 'all') return sessions;
    
    const now = new Date();
    const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
    const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
    
    return sessions.filter(session => {
      const sessionDate = new Date(session.createdAt);
      if (filter === 'week') return sessionDate >= weekAgo;
      if (filter === 'month') return sessionDate >= monthAgo;
      return true;
    });
  };

  const filteredSessions = getFilteredSessions();

  if (loading) {
    return (
      <div className="tracker-container">
        <div className="loading">Loading your sessions...</div>
      </div>
    );
  }

  return (
    <div className="tracker-container">
      <div className="tracker-header">
        <h1>Progress Tracker</h1>
        <p>View and manage your mindfulness journey</p>
      </div>

      {/* Stats Summary */}
      <div className="stats-summary">
        <div className="stat-box">
          <div className="stat-icon">üìä</div>
          <div className="stat-number">{stats.totalSessions}</div>
          <div className="stat-label">Total Sessions</div>
        </div>

        <div className="stat-box">
          <div className="stat-icon">‚è±Ô∏è</div>
          <div className="stat-number">{stats.totalMinutes}</div>
          <div className="stat-label">Minutes</div>
        </div>

        <div className="stat-box">
          <div className="stat-icon">üî•</div>
          <div className="stat-number">{stats.currentStreak}</div>
          <div className="stat-label">Week Streak</div>
        </div>
      </div>

      {/* Filter */}
      <div className="filter-section">
        <button 
          className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
          onClick={() => setFilter('all')}
        >
          All Time
        </button>
        <button 
          className={`filter-btn ${filter === 'week' ? 'active' : ''}`}
          onClick={() => setFilter('week')}
        >
          This Week
        </button>
        <button 
          className={`filter-btn ${filter === 'month' ? 'active' : ''}`}
          onClick={() => setFilter('month')}
        >
          This Month
        </button>
      </div>

      {/* Sessions List */}
      <div className="sessions-section">
        <h2>Your Sessions ({filteredSessions.length})</h2>
        
        {filteredSessions.length === 0 ? (
          <div className="empty-state">
            <p>No sessions found for this period.</p>
          </div>
        ) : (
          <div className="sessions-table">
            {filteredSessions.map((session) => (
              <div key={session._id} className="session-row">
                <div className="session-info">
                  <div className="session-main">
                    <span className="session-exercise">{session.exerciseType}</span>
                    <span className="session-duration">{session.duration} min</span>
                  </div>
                  <div className="session-date">{formatDate(session.createdAt)}</div>
                </div>

                <div className="session-mood">
                  {getMoodEmoji(session.mood)} {session.mood}
                </div>

                {session.notes && (
                  <div className="session-notes-preview">
                    üí≠ {session.notes}
                  </div>
                )}

                <button 
                  className="btn-delete"
                  onClick={() => deleteSession(session._id)}
                  title="Delete session"
                >
                  üóëÔ∏è
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Tracker;
