import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../config/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  console.log('AuthProvider rendered', { user, loading });

  useEffect(() => {
    console.log('AuthProvider useEffect - checking auth');
    checkAuth();
  }, []);

  const checkAuth = async () => {
    console.log('checkAuth called');
    const token = localStorage.getItem('token');
    console.log('Token from localStorage:', token ? 'exists' : 'null');

    if (token) {
      try {
        console.log('Fetching user profile...');
        const response = await api.get('/user/profile');
        console.log('Profile response:', response.data);
        setUser(response.data);
      } catch (error) {
        console.error('Auth check failed:', error);
        localStorage.removeItem('token');
      }
    }
    setLoading(false);
    console.log('checkAuth completed, loading set to false');
  };

  const login = async (email, password) => {
    console.log('=== LOGIN FUNCTION CALLED ===');
    console.log('Email:', email);
    console.log('Password length:', password.length);

    try {
      console.log('Making API request to /auth/login...');
      const response = await api.post('/auth/login', {
        email,
        password
      });

      console.log('Login API response:', response);
      console.log('Response data:', response.data);

      const { token, user } = response.data;
      console.log('Token received:', token ? 'YES' : 'NO');
      console.log('User received:', user);

      console.log('Saving token to localStorage...');
      localStorage.setItem('token', token);
      console.log('Token saved');

      console.log('Setting user state...');
      setUser(user);
      console.log('User state set');

      console.log('=== LOGIN FUNCTION SUCCESS ===');
      return response.data;

    } catch (error) {
      console.error('=== LOGIN FUNCTION ERROR ===');
      console.error('Error object:', error);
      console.error('Error response:', error.response);
      console.error('Error response data:', error.response?.data);
      console.error('Error message:', error.message);
      throw error;
    }
  };

  const register = async (name, email, password) => {
    console.log('=== REGISTER FUNCTION CALLED ===');
    console.log('Name:', name);
    console.log('Email:', email);

    try {
      console.log('Making API request to /auth/register...');
      const response = await api.post('/auth/register', {
        name,
        email,
        password
      });

      console.log('Register API response:', response.data);

      const { token, user } = response.data;
      localStorage.setItem('token', token);
      setUser(user);

      console.log('=== REGISTER FUNCTION SUCCESS ===');
      return response.data;

    } catch (error) {
      console.error('=== REGISTER FUNCTION ERROR ===');
      console.error('Error:', error);
      console.error('Error response:', error.response?.data);
      throw error;
    }
  };

  const logout = () => {
    console.log('Logout called');
    localStorage.removeItem('token');
    setUser(null);
  };

  const value = {
    user,
    loading,
    register,
    login,
    logout
  };

  console.log('AuthProvider value:', { user: !!user, loading });

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
